|-7| &= -(-7) = 7
\end{aligned}

\begin{aligned}
\text{For } |x| > 2, \text{ we have: } \\
x < -2 \quad \text{or} \quad x > 2 \\
\text{Solution set: } (-\infty, -2) \cup (2, \infty)
\end{aligned}

\begin{aligned}
0 < x \leq 5 \text{ means } x \text{ is greater than } 0 \text{ and less than or equal to } 5 \\
\text{In interval notation: } (0, 5]
\end{aligned}

\begin{aligned}
N_2(5) &= \{ x \in \mathbb{R} : |x - 5| < 2 \} \\
|x - 5| < 2 &\implies -2 < x - 5 < 2 \\
&\implies 3 < x < 7 \\
\text{Thus, } N_2(5) &= (3, 7)
\end{aligned}

\begin{aligned}
N_1(3) \setminus \{3\} &= \{ x : 0 < |x - 3| < 1 \} \\
0 < |x - 3| < 1 &\implies 0 < |x - 3| < 1 \implies 2 < x < 4, \ x \neq 3 \\
\text{Thus: } (2, 4) \setminus \{3\}
\end{aligned}

\begin{aligned}
\text{For any } x \in (0, 1), \text{ choose } \delta = \min(x, 1 - x) \\
N_{\delta}(x) = (x - \delta, x + \delta) \subseteq (0, 1) \text{ since } 0 < x - \delta < x + \delta < 1 \\
\text{Thus, } (0, 1) \text{ is open.} \ \checkmark
\end{aligned}

\begin{aligned}
\text{Limit points of } [0, 1] \text{ are points in } [0, 1], \text{ since any sequence in } [0, 1] \text{ converges to a point in } [0, 1] \\
\text{Boundary points } 0 \text{ and } 1 \text{ are in } [0, 1] \\
\text{Thus, } [0, 1] \text{ is closed.} \ \checkmark
\end{aligned}

\begin{aligned}
\text{As } x \to \infty, \frac{1}{x} \text{ gets smaller: } \\
\left| \frac{1}{x} - 0 \right| = \frac{1}{x} < \varepsilon \text{ if } x > \frac{1}{\varepsilon} \\
\text{Choose } M = \frac{1}{\varepsilon}, \text{ so for } x > M, \left| \frac{1}{x} \right| < \varepsilon \\
\text{Thus, } \lim_{x \to \infty} \frac{1}{x} = 0
\end{aligned}

\begin{aligned}
\text{A point } x_0 \text{ is a cluster point if every } N_{\delta}(x_0) \text{ contains points of } (0, 1) \text{ other than } x_0 \\
\text{For } x_0 \in (0, 1), \text{ e.g., } x_0 = 0.5, N_{\delta}(0.5) \subseteq (0, 1) \text{ for small } \delta, \text{ and contains other points} \\
\text{For } x_0 = 0 \text{ or } 1, N_{\delta}(0) \cap (0, 1) = (0, \delta) \neq \emptyset \\
\text{Thus, cluster points are } [0, 1]
\end{aligned}

\begin{aligned}
\text{For } S = \{5\}, \text{ take } \delta = 1: \\
N_1(5) = (4, 6) \cap \{5\} = \{5\} \\
\text{No other points of } S \text{ are in } (4, 6), \text{ so } 5 \text{ is isolated.} \ \checkmark
\end{aligned}

\begin{aligned}
\text{For } x \in (0, 1), \text{ we have } 0 < x < 1 \\
\text{Choose } M = 1: |x| < 1 \leq M \text{ for all } x \in (0, 1) \\
\text{Thus, } (0, 1) \text{ is bounded.} \ \checkmark
\end{aligned}

\begin{aligned}
S = \{1, \frac{1}{2}, \frac{1}{3}, \dots\} \text{ is bounded since } 0 < \frac{1}{n} \leq 1 \\
\text{For } x_0 = 0, \text{ every } N_{\delta}(0) = (-\delta, \delta) \text{ contains } \frac{1}{n} \text{ for large } n \\
\text{Thus, } 0 \text{ is a cluster point.} \ \checkmark
\end{aligned}

