\text{For } x_1 < x_2, \text{ compare } f(x_1) = 2x_1 \text{ and } f(x_2) = 2x_2 \\
\text{Since } x_1 < x_2 \implies 2x_1 < 2x_2, \text{ so } f(x_1) < f(x_2) \\
\text{Thus, } f(x) \text{ is strictly increasing.} \ \checkmark
\end{aligned}

\begin{aligned}
f'(x) = -2x, \text{ set } f'(x) = 0 \implies x = 0 \\
f''(x) = -2 < 0 \text{ (concave down, so local max)} \\
f(0) = 0, \text{ for } x \in N_{\delta}(0), \ -x^2 \leq 0 \\
\text{Thus, } x = 0 \text{ is a local max with } f(0) = 0
\end{aligned}

\begin{aligned}
\lim_{x \to 1} x^2 = 1^2 = 1 \\
f(1) = 1^2 = 1 \\
\text{Since } \lim_{x \to 1} f(x) = f(1), \text{ the function is continuous at } x = 1 \ \checkmark
\end{aligned}

\begin{aligned}
f(x) = x^2, \ f(x + h) = (x + h)^2 = x^2 + 2xh + h^2 \\
\frac{f(x + h) - f(x)}{h} &= \frac{(x^2 + 2xh + h^2) - x^2}{h} = \frac{2xh + h^2}{h} = 2x + h \\
\lim_{h \to 0} (2x + h) &= 2x \\
\text{Thus, } f'(x) = 2x
\end{aligned}

\begin{aligned}
\text{Using the power rule with } n = 5: \\
\frac{d}{dx} (x^5) &= 5 x^{5-1} = 5 x^4
\end{aligned}

\begin{aligned}
\text{Using the formula: } \\
\frac{d}{dx} (\tan x) &= \sec^2 x
\end{aligned}

\begin{aligned}
\text{Using the formula: } \\
\frac{d}{dx} (\tan^{-1} x) &= \frac{1}{1 + x^2}
\end{aligned}

\begin{aligned}
