\begin{aligned}
\text{Let } f(x) = x, \ g(x) = x + 1 \\
f'(x) = 1, \ g'(x) = 1 \\
\frac{d}{dx} \left( \frac{x}{x + 1} \right) &= \frac{f'(x) g(x) - f(x) g'(x)}{[g(x)]^2} \\
&= \frac{1 \cdot (x + 1) - x \cdot 1}{(x + 1)^2} \\
&= \frac{x + 1 - x}{(x + 1)^2} = \frac{1}{(x + 1)^2}
\end{aligned}

\begin{aligned}
F(x, y) = x^2 + y^2 - 1 = 0 \\
\frac{\partial F}{\partial x} = 2x, \ \frac{\partial F}{\partial y} = 2y \\
\frac{dy}{dx} &= -\frac{\frac{\partial F}{\partial x}}{\frac{\partial F}{\partial y}} = -\frac{2x}{2y} = -\frac{x}{y}
\end{aligned}

\begin{aligned}
f(x) = e^x \\
f'(x) = e^x, \ f''(x) = e^x, \ f'''(x) = e^x, \ \dots \\
\text{By induction, } f^{(n)}(x) = e^x \text{ for all } n
\end{aligned}

