\begin{aligned}
F_{A0} - F_A + r_A V &= 0 \\
10 - 4 + (-0.5) V &= 0 \\
6 &= 0.5 V \\
V &= \boxed{12 \, \text{m}^3}
\end{aligned}

\begin{aligned}
C_A = \frac{N_A}{V} = \frac{N_{A0} (1 - X_A)}{V}, \ r_A = -k \frac{N_{A0} (1 - X_A)}{V} \\
t &= N_{A0} \int_0^{0.5} \frac{dX_A}{- \left( -k \frac{N_{A0} (1 - X_A)}{V} \right) V} \\
&= \int_0^{0.5} \frac{dX_A}{k (1 - X_A)} = \frac{1}{k} [ -\ln (1 - X_A) ]_0^{0.5} \\
&= \frac{1}{0.05} \cdot ( -\ln 0.5 ) = 20 \cdot \ln 2 \\
&\approx \boxed{13.86 \, \text{s}}
\end{aligned}

\begin{aligned}
C_A = \frac{F_{A0} (1 - X_A)}{v_0}, \ r_A = -k \frac{F_{A0} (1 - X_A)}{v_0} \\
V &= F_{A0} \int_0^{0.5} \frac{dX_A}{- \left( -k \frac{F_{A0} (1 - X_A)}{v_0} \right)} \\
&= \frac{v_0}{k} \int_0^{0.5} \frac{dX_A}{1 - X_A} \\
&= \frac{10}{0.1} \cdot [ -\ln (1 - X_A) ]_0^{0.5} \\
&= 100 \cdot \ln 2 \approx \boxed{69.31 \, \text{m}^3}
\end{aligned}

\begin{aligned}
t &= \frac{1}{C_{A0} k (\theta_B - Z)} \ln \left( \frac{\theta_B - Z X_A}{1 - X_A} \right) - \ln \theta_B \\
&= \frac{1}{1 \cdot 0.05 \cdot (2 - 1)} \ln \left( \frac{2 - 1 \cdot 0.4}{1 - 0.4} \right) - \ln 2 \\
&= \frac{1}{0.05} \cdot \ln \left( \frac{1.6}{0.6} \right) - \ln 2 \\
&= 20 \cdot \ln \left( \frac{8}{3} \right) - \ln 2 \approx 19.6 - 0.693 \\
&= \boxed{18.91 \, \text{s}}
\end{aligned}

\begin{aligned}
V &= \frac{v_0}{k C_{A0} (\theta_B - Z)} \ln \left( \frac{\theta_B - Z X_A}{1 - X_A} \right) - \ln \theta_B \\
&= \frac{0.1}{0.05 \cdot 1 \cdot (2 - 1)} \ln \left( \frac{2 - 1 \cdot 0.4}{1 - 0.4} \right) - \ln 2 \\
&= \frac{0.1}{0.05} \cdot \ln \left( \frac{1.6}{0.6} \right) - \ln 2 \\
&= 2 \cdot \ln \left( \frac{8}{3} \right) - \ln 2 \approx 1.962 - 0.693 \\
&= \boxed{1.27 \, \text{m}^3}
\end{aligned}

\begin{aligned}
r_{AD} &= k_A P_A C_V - k_{-A} C_{A.S} \\
&= 0.01 \cdot 2 \cdot 0.1 - 0.05 \cdot 0.02 \\
&= 0.002 - 0.001 \\
&= \boxed{0.001 \, \text{mol/(g cat · s)}}
\end{aligned}

\begin{aligned}
r_{AD} &= k_A \left( P_A C_V - \frac{C_{A.S}}{K_A} \right) \\
&= 0.01 \cdot \left( 1.5 \cdot 0.2 - \frac{0.03}{0.25} \right) \\
&= 0.01 \cdot (0.3 - 0.12) \\
&= 0.01 \cdot 0.18 \\
&= \boxed{0.0018 \, \text{mol/(g cat · s)}}
\end{aligned}

\begin{aligned}
r_S &= k_S C_{A.S} C_V - \frac{C_{B.S} C_V}{K_S} \\
&= 0.02 \cdot 0.1 \cdot 0.2 - \frac{0.03 \cdot 0.2}{1.5} \\
&= 0.0004 - 0.004 \\
&= \boxed{-0.0036 \, \text{mol/(g cat · s)}}
\end{aligned}

\begin{aligned}
r_S &= k_S P_B C_{A.S} - \frac{C_{C.S}}{K_S} \\
&= 0.01 \cdot 1.5 \cdot 0.1 - \frac{0.02}{2} \\
&= 0.0015 - 0.01 \\
&= \boxed{-0.0085 \, \text{mol/(g cat · s)}}
\end{aligned}

\begin{aligned}
r_D &= k_D C_{C.S} - \frac{P_C C_V}{K_{D,C}} \\
&= 0.05 \cdot 0.03 - \frac{1 \cdot 0.2}{0.5} \\
&= 0.0015 - 0.4 \\
&= \boxed{-0.3985 \, \text{mol/(g cat · s)}}
\end{aligned}

\begin{aligned}
N_{A0} &= C_{A0} V = 1 \cdot 0.2 = 0.2 \, \text{mol} \\
C_A &= C_{A0} e^{-kt} = 1 \cdot e^{-0.05 \cdot 10} = e^{-0.5} \approx 0.6065 \, \text{mol/m}^3 \\
N_A &= C_A V = 0.6065 \cdot 0.2 = 0.1213 \, \text{mol} \\
N_{A0} - N_A &= 0.2 - 0.1213 \\
&= \boxed{0.0787 \, \text{mol}}
\end{aligned}
