\begin{aligned}
F(x, y, y', y'') &= y'' + 3y' + 2y \\
&= 0 \\
\text{This is a second-order linear DE.}
\end{aligned}

\begin{aligned}
\text{The highest derivative is } \frac{dy}{dx} \text{ (first order).} \\
\text{Order} &= \boxed{1}
\end{aligned}

\begin{aligned}
\text{Highest derivative is } y'' \text{ raised to power 3.} \\
\text{Degree} &= \boxed{3}
\end{aligned}

\begin{aligned}
\text{It involves one independent variable } x \text{ and derivatives w.r.t. } x. \\
\text{It is an} \boxed{\text{ODE}}
\end{aligned}

\begin{aligned}
\text{It has partial derivatives w.r.t. } x \text{ and } y. \\
\text{It is a} \boxed{\text{PDE}}
\end{aligned}

\begin{aligned}
y \text{ and derivatives are to first power.} \\
\text{Yes, it is} \boxed{\text{linear}}
\end{aligned}

\begin{aligned}
y \text{ is squared.} \\
\text{No, it is} \boxed{\text{non-linear}}
\end{aligned}

\begin{aligned}
y &= c \sin x \\
\frac{dy}{dx} &= c \cos x \\
\frac{dy}{dx} / y &= \cot x \\
\frac{dy}{dx} &= y \cot x \\
\boxed{\frac{dy}{dx} = y \cot x}
\end{aligned}

\begin{aligned}
y' = y \implies \frac{dy}{y} = dx \\
\ln |y| = x + c \\
y = Ae^x \\
y(0) = 1 \implies A = 1 \\
\boxed{y = e^x}
\end{aligned}

\begin{aligned}
y = A \cos x + B \sin x \\
y(0) = 0 \implies A = 0 \\
y = B \sin x \\
y(\pi) = 0 \implies B \sin \pi = 0 \\
\text{Trivial solution } \boxed{y = 0}
\end{aligned}

\begin{aligned}
y = ce^{2x} \\
y(0) = 3 \implies c = 3 \\
\boxed{y = 3e^{2x}}
\end{aligned}

\begin{aligned}
\frac{dy}{y} = x \, dx \\
\ln |y| = \frac{x^2}{2} + c \\
y &= Ae^{x^2 / 2} \\
\boxed{y = Ae^{x^2 / 2}}
\end{aligned}

\begin{aligned}
f(tx, ty) &= \frac{(tx)^2}{ty} = \frac{t^2 x^2}{t y} = t \cdot \frac{x^2}{y} = t^1 f(x,y) \\
\text{Homogeneous of degree } \boxed{1}
\end{aligned}

\begin{aligned}
\frac{\partial M}{\partial y} &= 2x + 1, \ \frac{\partial N}{\partial x} = 2x + 1 \\
\text{Exact.} \\
\int M \, dx &= x^2 y + y x + h(y) \\
\frac{\partial}{\partial y} &= x^2 + x + h'(y) = N = x^2 + x \\
h'(y) = 0 \implies h = c \\
\boxed{x^2 y + x y = c}
\end{aligned}

\begin{aligned}
M = y, N = x + y^2 \\
\frac{\partial M}{\partial y} - \frac{\partial N}{\partial x} = 1 - 1 = 0 \\
\text{Already exact.} \text{ (Trivial case)} \\
\boxed{\mu = 1}
\end{aligned}

\begin{aligned}
\mu &= e^{\int 2 dx} = e^{2x} \\
e^{2x} y' + 2 e^{2x} y &= 1 \\
\frac{d}{dx} (y e^{2x}) &= 1 \\
y e^{2x} &= x + c \\
y &= (x + c) e^{-2x} \\
\boxed{y = (x + c) e^{-2x}}
\end{aligned}

\begin{aligned}
r^2 - 3r + 2 = 0 \implies (r-1)(r-2) = 0 \\
r = 1, 2 \\
y &= c_1 e^x + c_2 e^{2x} \\
\boxed{y = c_1 e^x + c_2 e^{2x}}
\end{aligned}

\begin{aligned}
y_2 = v e^x \\
y_2' = v' e^x + v e^x \\
y_2'' = v'' e^x + 2 v' e^x + v e^x \\
\text{Plug in: } v'' e^x + 2 v' e^x + v e^x - 2(v' e^x + v e^x) + v e^x = 0 \\
v'' e^x = 0 \implies v'' = 0 \\
v' = c, v = c x + d \\
y_2 = x e^x \ (c=1, d=0) \\
\boxed{y_2 = x e^x}
\end{aligned}

\begin{aligned}
y_p = A \cos x + B \sin x \\
y_p' = -A \sin x + B \cos x \\
y_p'' = -A \cos x - B \sin x \\
-A \cos x - B \sin x + A \cos x + B \sin x = \sin x \\
0 = \sin x \text{ (wrong guess, use resonance)} \\
\text{For resonance, guess } y_p = x (A \cos x + B \sin x) \\
\text{After calculation: } A = 0, B = -1/2 \\
\boxed{y_p = -\frac{1}{2} x \cos x}
\end{aligned}

\begin{aligned}
(D-1)(D-2)y = 0 \\
\text{Solutions: } e^x, e^{2x} \\
\boxed{y = c_1 e^x + c_2 e^{2x}}
\end{aligned}

\begin{aligned}
F(s) &= \int_0^\infty e^{-st} e^{at} \, dt = \int_0^\infty e^{(a-s)t} \, dt \\
&= \left[ \frac{e^{(a-s)t}}{a-s} \right]_0^\infty = \frac{1}{s - a} \ (s > a) \\
\boxed{\mathcal{L}\{e^{at}\} = \frac{1}{s - a}}
\end{aligned}

\begin{aligned}
\mathcal{L}\{\sin 2t\} = \frac{2}{s^2 + 4} \implies \frac{1}{s^2 + 4} = \frac{1}{2} \cdot \frac{2}{s^2 + 4} \\
\mathcal{L}^{-1} \left\{ \frac{1}{s^2 + 4} \right\} = \frac{1}{2} \sin 2t \\
\boxed{f(t) = \frac{1}{2} \sin 2t}
\end{aligned}

\begin{aligned}
\mathcal{L}\{y''\} &= s^2 Y(s) - s \cdot 1 - 0 \\
&= s^2 Y(s) - s \\
\boxed{s^2 Y(s) - s}
\end{aligned}
