\begin{aligned}
P_T &= P_{\text{N}_2} + P_{\text{O}_2} \\
&= 0.8 + 0.6 = 1.4 \text{ atm} \\
\text{Thus, the total pressure is } 1.4 \text{ atm} \ \checkmark
\end{aligned}

\begin{aligned}
P_{\text{N}_2} &= X_{\text{N}_2} P_T \\
&= 0.4 \cdot 2.5 = 1.0 \text{ atm} \\
\text{Thus, the partial pressure of } \text{N}_2 \text{ is } 1.0 \text{ atm} \ \checkmark
\end{aligned}

\begin{aligned}
n &= n_{\text{O}_2} + n_{\text{N}_2} = 0.5 + 1.5 = 2.0 \text{ mol} \\
X_{\text{O}_2} &= \frac{n_{\text{O}_2}}{n} = \frac{0.5}{2.0} = 0.25 \\
\text{Thus, the mole fraction of } \text{O}_2 \text{ is } 0.25 \ \checkmark
\end{aligned}

\begin{aligned}
M &= 28.02 \text{ g/mol} = 0.02802 \text{ kg/mol} \\
u_{\text{rms}} &= \sqrt{\frac{3RT}{M}} = \sqrt{\frac{3 \cdot 8.314 \cdot 300}{0.02802}} \\
&= \sqrt{\frac{7476.6}{0.02802}} \approx \sqrt{266832.3} \approx 516.6 \text{ m/s} \\
\text{Thus, } u_{\text{rms}} \approx 516.6 \text{ m/s} \ \checkmark
\end{aligned}

\begin{aligned}
