\begin{aligned}
\text{Using the standard derivative formula: } \\
\frac{d}{dx} (\sin x) &= \cos x
\end{aligned}

\begin{aligned}
\text{Using the chain rule: Let } u = 3x, \ \frac{du}{dx} = 3 \\
\frac{d}{dx} (\cos u) &= -\sin u \cdot \frac{du}{dx} \\
&= -\sin(3x) \cdot 3 = -3 \sin(3x)
\end{aligned}

\begin{aligned}
\text{Using the chain rule: Let } u = 2x, \ \frac{du}{dx} = 2 \\
\frac{d}{dx} (\tan u) &= \sec^2 u \cdot \frac{du}{dx} \\
&= \sec^2(2x) \cdot 2 = 2 \sec^2(2x)
\end{aligned}

\begin{aligned}
\text{Using the standard derivative formula: } \\
\frac{d}{dx} (\cot x) &= -\csc^2 x
\end{aligned}

\begin{aligned}
\text{Using the chain rule: Let } u = 4x, \ \frac{du}{dx} = 4 \\
\frac{d}{dx} (\sec u) &= \sec u \tan u \cdot \frac{du}{dx} \\
&= \sec(4x) \tan(4x) \cdot 4 = 4 \sec(4x) \tan(4x)
\end{aligned}

\begin{aligned}
\text{Using the standard derivative formula: } \\
\frac{d}{dx} (\csc x) &= -\csc x \cot x
\end{aligned}

\begin{aligned}
\text{Let } f(x) = \sin x, \ g(x) = \cos x \\
f'(x) = \cos x, \ g'(x) = -\sin x \\
\frac{d}{dx} (\sin x \cos x) &= \cos x \cdot \cos x + \sin x \cdot (-\sin x) \\
&= \cos^2 x - \sin^2 x = \cos 2x
\end{aligned}

\begin{aligned}
\int 3 \sin 2x \, dx &= 3 \int \sin 2x \, dx \\
\text{Let } u = 2x, \ du = 2 \, dx, \ dx = \frac{du}{2} \\
&= 3 \cdot \frac{1}{2} \int \sin u \, du = \frac{3}{2} (-\cos u) + C \\
&= -\frac{3}{2} \cos 2x + C
\end{aligned}

\begin{aligned}
\text{Let } u = 5x, \ du = 5 \, dx, \ dx = \frac{du}{5} \\
\int \cos (5x) \, dx &= \int \cos u \cdot \frac{du}{5} = \frac{1}{5} \int \cos u \, du \\
&= \frac{1}{5} \sin u + C = \frac{1}{5} \sin 5x + C
\end{aligned}

\begin{aligned}
\int \tan 3x \, dx &= \int \frac{\sin 3x}{\cos 3x} \, dx \\
\text{Let } u = \cos 3x, \ du = -3 \sin 3x \, dx, \ dx = \frac{du}{-3 \sin 3x} \\
&= \int \frac{1}{u} \cdot \left( -\frac{1}{3} \right) du = -\frac{1}{3} \int \frac{1}{u} \, du \\
&= -\frac{1}{3} \ln |u| + C = -\frac{1}{3} \ln |\cos 3x| + C
\end{aligned}

\begin{aligned}
\int \sec 2x \, dx &= \int \sec 2x \, dx \\
\text{Let } u = 2x, \ du = 2 \, dx, \ dx = \frac{du}{2} \\
&= \frac{1}{2} \int \sec u \, du = \frac{1}{2} \ln |\sec u + \tan u| + C \\
&= \frac{1}{2} \ln |\sec 2x + \tan 2x| + C
\end{aligned}

\begin{aligned}
\text{Using the standard integral formula: } \\
\int \csc x \, dx &= -\ln |\csc x + \cot x| + C
\end{aligned}

\begin{aligned}
\int \cot 4x \, dx &= \int \frac{\cos 4x}{\sin 4x} \, dx \\
\text{Let } u = \sin 4x, \ du = 4 \cos 4x \, dx, \ dx = \frac{du}{4 \cos 4x} \\
&= \int \frac{1}{u} \cdot \frac{du}{4} = \frac{1}{4} \int \frac{1}{u} \, du \\
&= \frac{1}{4} \ln |u| + C = \frac{1}{4} \ln |\sin 4x| + C
\end{aligned}

\begin{aligned}
\text{Let } u = \sin x, \ du = \cos x \, dx \\
\int \sin x \cos x \, dx &= \int u \, du = \frac{u^2}{2} + C \\
&= \frac{1}{2} \sin^2 x + C
\end{aligned}

\begin{aligned}
\int \operatorname{arcsch} x \, dx &= x \, \operatorname{arcsch} x + \ln \left| x + \sqrt{1+x^2} \right| + C
\end{aligned}
